using FellowOakDicom;
using FellowOakDicom.IO.Buffer;
using System;
using System.IO;
using FellowOakDicom.Imaging;
using SixLabors.Fonts;
using SixLabors.ImageSharp;
using SixLabors.ImageSharp.Processing;
using SixLabors.ImageSharp.Drawing.Processing;
using SixLabors.ImageSharp.PixelFormats;

namespace BadMedicine.Dicom;

/// <summary>
/// Handles drawing image data directly into the dicom file
/// </summary>
internal class PixelDrawer
{
    // https://cannotintospacefonts.blogspot.com/
    // Converted via https://cloudconvert.com/
    private static readonly Font Font = new FontCollection().Add(new MemoryStream(Convert.FromBase64String(""))).CreateFont(12,FontStyle.Regular);
    internal static void DrawBlackBoxWithWhiteText(DicomDataset ds, int width, int height, string msg)
    {
        var buffer=new byte[width * height * 3];
        using var img=new Image<Rgb24>(width, height);
        img.Mutate(x => x.Fill(Color.Black));
        img.Mutate(x=>x.DrawText(msg,Font,Color.White,new PointF(width/2f,height/2f)));
        img.CopyPixelDataTo(buffer);

        ds.Add(DicomTag.PhotometricInterpretation, PhotometricInterpretation.Rgb.Value);
        ds.Add(DicomTag.Rows, (ushort)img.Height);
        ds.Add(DicomTag.Columns, (ushort)img.Width);
        ds.Add(DicomTag.BitsAllocated, (ushort)8);

        var pixelData = DicomPixelData.Create(ds, true);
        pixelData.BitsStored = 8;
        pixelData.SamplesPerPixel = 3;
        pixelData.HighBit = 7;
        pixelData.PixelRepresentation = 0;
        pixelData.PlanarConfiguration = 0;
        pixelData.AddFrame(new MemoryByteBuffer(buffer));
    }
}